import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import "./singlePost.css";

export default function SinglePost() {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const location = useLocation()
    const path = location.pathname.split("/")[2]

    const [post, setPost] = useState({});

    const publicFolder = "http://localhost:5000/images/";

    useEffect(() => {
        const getPost = async () => {
            const res = await axios.get("/posts/" + path)
            setPost(res.data)
        };
        getPost()
    }, [path])
    return (
        <div className="singlePost">
            <div className="singlePostWrapper">
                <img className="singlePostImg" src={publicFolder + post.photo} alt="Blog-post" />
                <h1 className="singlePostTitle">
                    {post.username === user.username} && {
                    <div className="singlePostEdit">
                        <i className="singlePostIcon far fa-edit"></i>
                        <i className="singlePostIcon far fa-trash-alt"></i>
                    </div>
                    }
                    {post.title}
                </h1>
                <div className="singlePostInfo">
                    <span className="singlePostAuthor">Author: <Link to={`/?user=${post.username}`} className="link"><b>{post.username}</b></Link></span>
                    <span className="singlePostDate">{new Date(post.updatedAt).toDateString()}</span>
                </div>
                <p className="singlePostDescription">{post.desc}</p>
            </div>
        </div>
    )
}
