import "./post.css"

export default function post(props) {
    return (
        <div className="post">
            {props.posts.photo && (
                
            <img className="postImg" src={props.posts.photo} alt="blog" />
            )}
            <div className="postInfo">
                <div className="postCats">
                    <span className="postCat">Music</span>
                    <span className="postCat">Tech</span>
                </div>
                <span className="postTitle">
                    {props.posts.title}
                </span>
                <hr />
                <span className="postDate">{new Date (props.posts.createdAt).toDateString()}</span>
            </div>
            <p className="postDescription">{props.posts.desc}</p>
        </div>
    )
}
