import axios from "axios";
import { useContext, useState } from "react"
import { Context } from "../../context/Context";
import "./write.css"

export default function Write() {

    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [file, setFile] = useState(null);
    const {user} = useContext(Context);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = {
            username: user.username,
            title,
            desc,
        }
        if (file) {
            const data = FormData();
            const filename = Date.now() + file.name;
            data.append("name", filename)
            data.append("file", file)
        }
        axios.post("/posts",);
    }

    return (
        <div className="write">
        <img className="writeImg" src="https://images.pexels.com/photos/1591056/pexels-photo-1591056.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260" alt="Blog" />
            <form className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                        <i className="writeIcon fas fa-plus"></i>
                    </label>
                    <input className="addFileButton" type="file" id="fileInput" />
                    <input className="writeInput" type="text" placeholder="Title" autoFocus={true}/>
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder="Write your story here..." type="text" className="writeInput writeText" cols="30" rows="10"></textarea>
                </div>
                <button className="writeSubmit">
                    Publish
                </button>
            </form>
        </div>
    )
}
