import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./singlePost.css";

export default function SinglePost() {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const location = useLocation()
    const path = location.pathname.split("/")[2]
    
    const [post, setPost] = useState({});

    useEffect(() => {
        const getPost = async () => {
            const res = await axios.get("/posts/" + path)
            setPost(res.data)
        };
        getPost()
    }, [path])
    return (
        <div className="singlePost">
            <div className="singlePostWrapper">
                <img className="singlePostImg" src="https://images.pexels.com/photos/1591056/pexels-photo-1591056.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260" alt="Blog-post" />
                <h1 className="singlePostTitle">
                    Lorem ipsum dolor sit amet.
                    <div className="singlePostEdit">
                        <i className="singlePostIcon far fa-edit"></i>
                        <i className="singlePostIcon far fa-trash-alt"></i>
                    </div>
                </h1>
                <div className="singlePostInfo">
                    <span className="singlePostAuthor">Author: <b>Mustafa</b></span>
                    <span className="singlePostDate">1 hour ago</span>
                </div>
                <p className="singlePostDescription">Lorem ipsum dolor sit amet consectetur, adipisicing elit. Numquam repellendus voluptate quod optio cupiditate culpa laudantium tenetur sint quia nisi velit, doloribus aspernatur cum eligendi fugiat sed eos natus temporibus libero. Fugit labore iste perferendis ad necessitatibus ex. Molestiae, vero exercitationem aperiam officiis beatae soluta a quis, illum repellendus sed ducimus, dolorum magni tempora iste voluptatum. Nihil iste cum perferendis fugiat veniam molestiae cumque temporibus pariatur quos mollitia. Ipsa, doloremque labore minus explicabo veritatis a magnam, dolorum consequuntur voluptatem ducimus deleniti non aspernatur. Voluptate pariatur fugiat officiis magnam molestiae minus! Ipsum ex ipsam nulla itaque, quos dolorum inventore aperiam quam?</p>
            </div>
        </div>
    )
}
